import React from 'react'
import { Scrollbars } from 'react-custom-scrollbars';
import { IconButton } from "@chakra-ui/button";
import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  useDisclosure,
} from "@chakra-ui/react"
import { Button, ButtonGroup } from "@chakra-ui/react"
import { useColorMode } from "@chakra-ui/color-mode";
import { Flex, VStack, Heading, Spacer } from "@chakra-ui/layout";
import { FaSun, FaMoon, FaInstagram, FaGithub, FaLinkedin ,FaBars, FaDownload} from 'react-icons/fa'
import Header from "./components/Header";
import Profile from "./components/Profile";
import Social from "./components/Social";
import BottomNav from "./components/BottomNav"
import SideNav from "./components/SideNav"
import { useMediaQuery } from '@chakra-ui/media-query';
import "./App.css"

function App() {
  const { colorMode, toggleColorMode } = useColorMode();
  const isDark = colorMode === "dark";
  const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
  const { isOpen, onOpen, onClose } = useDisclosure()
  const btnRef = React.useRef()

  return (
    // <Scrollbars style={{ width: "100%", height: "100vh" }}>
    <VStack p={5}>

      <Flex w="100%"  style = {!isNotSmallerScreen ? {position:"fixed",top:0,left:0,padding:10} : null}    bg ={!isNotSmallerScreen ? !isDark?"rgba(255, 255, 255)":"rgba(34, 41, 59, 0.918)" : null } >
        <Heading
          ml="8" size="md" alignSelf="center" fontWeight='semibold' color="cyan.400">Expl◉re Me!</Heading>
        <Spacer/>
        {isNotSmallerScreen ? 
          <>
        <Button aria-label = "Home" icon={<FaLinkedin />} isRound='true' >Home</Button>
        <Button variant="ghost" ml={2} icon={<FaInstagram />} isRound='true' >About</Button>
        <Button variant="ghost" ml={2} icon={<FaGithub />} isRound='true'>Projects</Button>
        <Button variant="ghost" ml={2} icon={<FaGithub />} isRound='true' >Skills</Button>
        <Button variant="ghost" ml={2} icon={<FaGithub />} isRound='true' >Contact</Button>
        <IconButton  ml={7} mr={4} icon={isDark ? <FaSun /> : <FaMoon />} isRound='true' onClick={toggleColorMode}></IconButton>
          </> : 
          <>
          <Button ref={btnRef} isRound='true' onClick={onOpen}>
            <FaBars/>
          </Button>
          <Drawer
            isOpen={isOpen}
            placement="bottom"
            onClose={onClose}
            finalFocusRef={btnRef}

          >
            <DrawerOverlay />
            <DrawerContent>
              <DrawerCloseButton />
              <DrawerHeader>✆ +91-9210748848</DrawerHeader>

              <DrawerBody>
              <Flex  flexDirection = "column" alignItems="center" justifyContent = "center">
              <Button size="lg" aria-label = "Home" icon={<FaLinkedin />} isRound='true' >Home</Button>
              <Button size="lg" variant="ghost" my={4} ml={2} icon={<FaInstagram />} isRound='true' >About</Button>
              <Button size="lg" variant="ghost" mt={4} ml={2} icon={<FaGithub />} isRound='true' >Projects</Button>
              <Button size="lg" variant="ghost" mt={4} ml={2} icon={<FaGithub />} isRound='true' >Skills</Button>
              <Button size="lg" variant="ghost" my={4} ml={2} icon={<FaGithub />} isRound='true' >Contact</Button>
              </Flex>
              </DrawerBody>

              <DrawerFooter>
              <IconButton mx={2} icon={isDark ? <FaSun /> : <FaMoon />} isRound='true' onClick={toggleColorMode}></IconButton>
              <Spacer/>
                <Button variant="outline" mr={3} onClick={onClose}>
                  Hire Me
                </Button>

                <Button colorScheme="blue"> <FaDownload/> &nbsp;Resume</Button>
              </DrawerFooter>
            </DrawerContent>
          </Drawer>
        </>
          }

      </Flex>
      <Header/>
      <Social/>
      <Profile/>
      {!isNotSmallerScreen?<BottomNav/>:null}
      {isNotSmallerScreen?<SideNav/>:null}
    </VStack>
    // </Scrollbars>
  );
}

export default App;
