import React from 'react'
import { Tabs, TabList, TabPanels, Tab, TabPanel } from "@chakra-ui/react"
import { Image } from "@chakra-ui/image";
import { Stack, Circle, Flex, Box, Text} from "@chakra-ui/layout";
import { useMediaQuery } from "@chakra-ui/media-query";
import { useColorMode } from "@chakra-ui/color-mode";


const ProjectTab = () => {
  const { colorMode } = useColorMode();
  const isDark = colorMode === "dark";
  const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
  const colors = useColorModeValue(
    ["red.50", "teal.50", "blue.50"],
    ["red.900", "teal.900", "blue.900"],
  )
  const [tabIndex, setTabIndex] = React.useState(0)
  const bg = colors[tabIndex]
    return (
        <Stack p = {5} style = {{outline :"none"}} justifyContent = "flex-start" alignItems = "center"  id="project" style={{overflow: 'hidden'}}  direction={isNotSmallerScreen ? "row" : "column-reverse"} >
                <Tabs onChange={(index) => setTabIndex(index)} bg={bg} variant="soft-rounded" colorScheme="blue">
                    <TabList>
                        <Tab>Our Projects</Tab>
                        <Tab>Commercial projects</Tab>
                        <Tab>Residential projects</Tab>
                    </TabList>

                    <TabPanels>
                        <TabPanel>
                        <p>one!</p>
                        </TabPanel>
                        <TabPanel>
                        <p>two!</p>
                        </TabPanel>
                        <TabPanel>
                        <p>three!</p>
                        </TabPanel>
                    </TabPanels>
                </Tabs>
        </Stack>
    )
}

export default ProjectTab
