import React from "react";
import { Image } from "@chakra-ui/image";
import { useColorMode } from "@chakra-ui/color-mode";
import { useMediaQuery } from "@chakra-ui/media-query";
import { Stack, Circle, Flex, Box, Text, Heading } from "@chakra-ui/layout";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import Skill from './Skill'

const Skills = () => {
    const { colorMode } = useColorMode();
    const isDark = colorMode === "dark";
    const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
    return (
        <Stack w = "100%" p = {6} alignItems="center" justify="center">
          <Flex w="80%" bg = {isDark ? "gray.600" : "cyan.500"}  borderRadius = "30" 
          direction={isNotSmallerScreen ? "row" : "column"}
        //   spacing="200px"
          justifyContent = "space-evenly"
          p={isNotSmallerScreen ? "25" : "0"}
          alignSelf="flex-start"
          alignItems="center"
        //   flexWrap={isNotSmallerScreen ? "wrap" : null}
          mb = {12}>
                <Skill
                    skillvalue = {80}
                    skillcolor = "#fcba03"
                    skillsub = "ReactJS"
                    skilldesc = "Handsome experience in ReactJS"
                />
                <Skill
                    skillvalue = {80}
                    skillcolor = "blue"
                    skillsub = "JavaScript"
                    skilldesc = "Advance JS/ Jquery"
                />
                <Skill
                    skillvalue = {80}
                    skillcolor = "red"
                    skillsub = "JavaScript"
                    skilldesc = "Advance JS/ Jquery"
                />
                <Skill
                    skillvalue = {80}
                    skillcolor = "red"
                    skillsub = "JavaScript"
                    skilldesc = "Advance JS/ Jquery"
                />
                <Skill
                    skillvalue = {80}
                    skillcolor = "red"
                    skillsub = "JavaScript"
                    skilldesc = "Advance JS/ Jquery"
                />
                <Skill
                    skillvalue = {80}
                    skillcolor = "red"
                    skillsub = "JavaScript"
                    skilldesc = "Advance JS/ Jquery"
                />
          </Flex>  
        </Stack>
    )
}

export default Skills
