import React from 'react'
import { IconButton } from "@chakra-ui/button";
import { useColorMode } from "@chakra-ui/color-mode";
import { Flex, VStack, Heading, Spacer } from "@chakra-ui/layout";
import { FaSun, FaMoon, FaInstagram, FaGithub, FaLinkedin } from 'react-icons/fa'
import { useMediaQuery } from '@chakra-ui/media-query';

const SideNav = () => {
    const { colorMode, toggleColorMode } = useColorMode();
    const isDark = colorMode === "dark";
    const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
    return (
        <VStack p={5} style={{position:"fixed",top:"40%",right:"0px",}} bg = {isDark ? "black" : "blue.100"}>

        <Flex  flexDirection = "column" alignItems="center" justifyContent = "center">  
            <IconButton mt={5} icon={<FaLinkedin />} isRound='true' onClick={() =>window.open("https://www.linkedin.com/in/sanchay-sethi/")}></IconButton>
            <IconButton mt={5} icon={<FaInstagram />} isRound='true' onClick={() =>window.open("https://www.instagram.com/sethi.sanchay/")}></IconButton>
            <IconButton mt={5} icon={<FaGithub />} isRound='true' onClick={() =>window.open("https://github.com/Sanchay-Sethi/")}></IconButton>
            <IconButton mt={5} icon={isDark ? <FaSun /> : <FaMoon />} isRound='true' onClick={toggleColorMode}></IconButton>
        </Flex>
        </VStack>
    )
}

export default SideNav
