                import { useCallback, useEffect, useRef } from "react";

const noop = (): void => {};

type EmptyCallback = () => void;

export type IntervalHookCallback = (ticks?: number) => void;
export type IntervalHookFinishCallback = () => void;
export type IntervalHookStartMethod = EmptyCallback;
export type IntervalHookStopMethod = (triggerFinishCallback?: boolean) => void;
export type IntervalHookIsActiveMethod = () => boolean;
export interface IntervalHookOptions {
  onFinish?: IntervalHookFinishCallback;
  autoStart?: boolean;
  immediate?: boolean;
  selfCorrecting?: boolean;
}

export type IntervalHookResult = {
  start: IntervalHookStartMethod;
  stop: IntervalHookStopMethod;
  isActive: IntervalHookIsActiveMethod;
};

export function useInterval(
  callback: IntervalHookCallback,
  interval = 1000,
  {
    onFinish = noop,
    autoStart = true,
    immediate = false,
    selfCorrecting = true,
  }: IntervalHookOptions = {}
): IntervalHookResult {
  const timer = useRef<NodeJS.Timeout>();
  const active = useRef<boolean>(false);
  const expected = useRef<number | null>(null);
  const savedCallback = useRef<IntervalHookCallback>(callback);

  const tick = useCallback(() => {
    /* istanbul ignore next */
    const expectedTimestamp = expected.current || 0;

    if (selfCorrecting) {
      const delay = Date.now() - expectedTimestamp;
      const ticks = 1 + (delay > 0 ? Math.floor(delay / interval) : 0);
      expected.current = expectedTimestamp + interval * ticks;
      set(Math.max(interval - delay, 1));
      savedCallback.current(ticks);
    } else {
      set(interval);
      savedCallback.current();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [interval]);

  const set = useCallback(
    (ms) => {
      if (timer.current !== undefined) {
        clearTimeout(timer.current);
      }
      if (active.current) {
        timer.current = setTimeout(tick, ms);
      } else {
        console.debug(
          "Trying to set interval timeout on inactive timer, this is no-op and probably indicates bug in your code."
        );
      }
    },
    [tick, active]
  );

  const start = useCallback(() => {
    const isActive = active.current;
    active.current = true;
    if (expected.current === null) {
      expected.current = Date.now() + interval;
    }
    if (immediate && !isActive) {
      expected.current -= interval;
      tick();
    }

    set(interval);
  }, [tick, interval, immediate, set]);

  const stop = useCallback(
    (triggerFinish = true) => {
      const isActive = active.current;
      if (timer.current !== undefined) {
        clearTimeout(timer.current);
      }
      active.current = false;
      timer.current = undefined;
      expected.current = null;
      if (isActive && triggerFinish) {
        onFinish();
      }
    },
    [onFinish]
  );

  const isActive = useCallback(() => active.current, []);

  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  useEffect(() => {
    autoStart && start();
    return stop;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return { start, stop, isActive };
}
import React, { useEffect, useState } from "react";
import Item from "./Item";
import "./styles.scss";
import { useInterval } from "./useInterval";

export interface IItem {
  id: string;
  image: string;
  imageStyles?: React.CSSProperties;
  imageClass?: string;
  topText?: JSX.Element;
  buttomText?: JSX.Element;
}

interface ImageGridProps {
  images: string[] | IItem[];
  visibleCount: number;
  interval: number;
  animationItemcount?: number;
  isActive?: boolean;
  transitionDuration: number;
  transitionType?: "SCALE" | "FADE" | "FADE_AND_SCALE" | "NONE";
  imageClass?: string;
  onItemClick?: (Item: IItem) => void;
}

const ImageGrid = (props: ImageGridProps) => {
  const [visibles, setVisibles] = useState<IItem[]>([]);
  const [invisibles, setInvisibles] = useState<IItem[]>([]);

  const { isActive: isActiveProp } = props;

  useEffect(() => {
    const items: IItem[] = (props.images as Array<string | IItem>).map(
      (image: string | IItem, index: number) => {
        if (typeof image === "string") {
          console.log("string");
          return {
            id: "none",
            image,
            topText: <></>,
            buttomText: <></>,
          };
        } else if (typeof image === "object") {
          return image;
        }
        return {
          id: "none",
          image,
          topText: <></>,
          buttomText: <></>,
        };
      }
    );
    const invs: IItem[] = [];
    const vs: IItem[] = [];

    
    let t_vc = 0;
    if(props.visibleCount>=items.length){
      t_vc=items.length-1;
    }else{
      t_vc=props.visibleCount;
    }

    items.forEach((item) => {
      if (vs.length >= t_vc) {
        invs.push(item);
      } else {
        vs.push(item);
      }
    });
    setVisibles(vs);
    setInvisibles(invs);
  }, [props.images, props.visibleCount]);

  const { start, stop } = useInterval(() => {
    let t_vc = 0;
    if(props.visibleCount>=props.images.length){
      t_vc=props.images.length-1;
    }else{
      t_vc=props.visibleCount;
    }
    if (visibles.length === t_vc) {
      const tc = props.images.length;
      let vc = t_vc;
      vc = (vc + (vc % 2)) / 2;
      const ic = tc - t_vc;
      let mxc = vc < ic ? vc : ic;
      const oneTimeCount =
        props.animationItemcount === 0
          ? Math.abs(Math.floor(Math.random() * mxc) + 1)
          : (props.animationItemcount ||
              props.images.length - t_vc) > mxc
          ? mxc
          : props.animationItemcount ||
            props.images.length - t_vc;
      let r_array01 = shuffleArray([...Array(visibles.length).keys()]),
        r_array02 = shuffleArray([...Array(invisibles.length).keys()]);
      r_array01 =
        Math.random() < 0.5
          ? r_array01.filter((n) => n % 2 === 0)
          : r_array01.filter((n) => n % 2 === 1);
      for (
        let i: number = 0;
        i < (oneTimeCount > r_array01.length ? r_array01.length : oneTimeCount);
        i++
      )
        [visibles[r_array01[i]], invisibles[r_array02[i]]] = [
          invisibles[r_array02[i]],
          visibles[r_array01[i]],
        ];
      setVisibles([...visibles]);
      setInvisibles([...invisibles]);
    }
  }, props.interval);

  useEffect(() => {
    if (isActiveProp) {
      start();
    } else {
      stop();
    }
  }, [isActiveProp, start, stop]);

  const shuffleArray = (array: number[]): number[] => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  return (
    <div>
      <div className="brand-animation-wraper">
        {visibles.map((item, index) => (
          <Item
            transitionDuration={props.transitionDuration}
            key={index}
            image={item}
            id={item.id}
            onClick={(id) => {
              if (props.onItemClick) props.onItemClick(item);
            }}
            transitionType={props.transitionType || "FADE_AND_SCALE"}
            imageClass={props.imageClass}
          />
        ))}
      </div>
    </div>
  );
};

export default ImageGrid;
