import { Button } from '@chakra-ui/button';
import { useColorMode } from '@chakra-ui/color-mode'
import { Image } from '@chakra-ui/image';
import { Stack, Circle, Flex, Box, Text } from '@chakra-ui/layout';
import { useMediaQuery } from '@chakra-ui/media-query';
import Typewriter from 'typewriter-effect'
import React from 'react'

function Header() {

    const { colorMode } = useColorMode();
    const isDark = colorMode === "dark";

    const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");

    return (
        <Stack style = {{overflow:"hidden"}}>
            <Circle zIndex = "-1"  position="absolute" bg="blue.100" opacity={isDark?"0.1":"0.4"}
                w="300px" h="300px" alignSelf="flex-end" />
            <Circle zIndex = "-1"  position="absolute" bg="blue.100" opacity={isDark?"0.1":"0.4"}
                w="100px" h="100px" alignSelf="flex-end" />
            <Flex direction={isNotSmallerScreen ? "row" : "column"}
                spacing="200px" p={isNotSmallerScreen ? "32" : "0"}
                alignSelf="flex-start">
                <Box mt={isNotSmallerScreen ? "0" : 16} align='flex-start'>
                    <Text fontSize="5xl" fontWeight="semibold">Hi<span className = "wave">👋</span>, I'm</Text>
                    <Text fontSize="7xl" fontWeight="bold" bgGradient="linear(to-r, cyan.400, blue.500, purple.600)" bgClip='text' >Sanchay Sethi</Text>
                    <Text fontSize="5xl" fontWeight="semibold"><span><Typewriter 
                    options={{
                        autoStart: true,
                        loop: true,
                      }}
                        onInit = {(typewriter)=>{
                            typewriter.pauseFor(1500)
                            .typeString('Full Stack Developer')
                            .pauseFor(600)
                            .deleteChars(9)

                            .typeString('Designer')
                            .pauseFor(2000)
                            .deleteAll()
                            .typeString('<strong>App</strong> Developer')
                            .pauseFor(2000)
                            .deleteAll()
                            .typeString('<strong>UI/UX</strong> Designer')
                            .pauseFor(2000)
                            .deleteAll()
                            .typeString('<strong>Freelancer</strong>')
                            .pauseFor(2000)
                            .start();
                        }} /></span> </Text>
                    <Text color={isDark ? "gray.200" : "gray.500"}>Expert at - Flutter, React Native, Firebase. Founder of Google & Amazon. <br/>
              Building @Modulewrapper, Freelancer & Entrepreneur 🗣</Text>
                    <Button mt={8} colorScheme="blue" onClick={() =>
                        window.open("https://sanchaysethi.herokuapp.com/")
                    }>Hire Me</Button>

                </Box>

                <Image m="50" alignSelf="center" mt={isNotSmallerScreen ? "0" : "12"}
                    mb={isNotSmallerScreen ? "0" : "12"} borderRadius='full'
                    backgroundColor="transparent" boxShadow="lg"
                    boxSize="300px" src='https://avatars.githubusercontent.com/Sanchay-Sethi
                    ' />
            </Flex>
            <Circle  zIndex = "-1" position="absolute" bg="blue.100" opacity={isDark?"0.1":"0.4"} top="280" left="-250"
                w="600px" h="600px" alignSelf="flex-start" />
            {isNotSmallerScreen?  <Circle  zIndex = "-1" position="absolute" bg="blue.100" opacity={isDark?"0.1":"0.4"} top="200" left="500"
                w="200px" h="200px" alignSelf="flex-start" /> : null}    

        </Stack>
    )
}

export default Header
