import { IconButton } from "@chakra-ui/button";
import { useColorMode } from "@chakra-ui/color-mode";
import { Flex, VStack, Heading, Spacer } from "@chakra-ui/layout";
import { FaSun, FaMoon, FaInstagram, FaGithub, FaLinkedin } from 'react-icons/fa'
import Header from "./components/Header";
import Profile from "./components/Profile";
import Social from "./components/Social";
import BottomNav from "./components/BottomNav"
import { useMediaQuery } from '@chakra-ui/media-query';
import "./App.css"

function App() {
  const { colorMode, toggleColorMode } = useColorMode();
  const isDark = colorMode === "dark";
  const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
  return (
    <VStack p={5}>
      <Flex w="100%" style = {{position:"fixed",top:20,left:0}}>
        <Heading
          ml="8" size="md" fontWeight='semibold' color="cyan.400">My Portfolio</Heading>
        <Spacer/>
        <IconButton   icon={<FaLinkedin />} isRound='true' onClick={() =>window.open("https://www.linkedin.com/in/sanchay-sethi/")}></IconButton>
        <IconButton  ml={2} icon={<FaInstagram />} isRound='true' onClick={() =>window.open("https://www.instagram.com/sethi.sanchay/")}></IconButton>
        <IconButton  ml={2} icon={<FaGithub />} isRound='true' onClick={() =>window.open("https://github.com/Sanchay-Sethi/")}></IconButton>
        <IconButton  ml={7} mr={4} icon={isDark ? <FaSun /> : <FaMoon />} isRound='true' onClick={toggleColorMode}></IconButton>
      </Flex>
      <Header/>
      <Social/>
      <Profile/>
      {!isNotSmallerScreen?<BottomNav/>:null}
    </VStack>
  );
}

export default App;
