import React, { useEffect, useState } from "react";
import { Image } from "@chakra-ui/image";
import { useColorMode } from "@chakra-ui/color-mode";
import { useMediaQuery } from "@chakra-ui/media-query";
import { Stack, Circle, Flex, Box, Text, Heading } from "@chakra-ui/layout";
import { Button } from "@chakra-ui/button";

const Footer = () => {
    const { colorMode } = useColorMode();
    const isDark = colorMode === "dark";
    const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
    return (
        <Stack className="footeredit" w = "100%" h = "300px" bg = {isDark ? "gray.600" : "cyan.100"} borderRadius = "20" mb = {12}>
            <Flex w = "80%" alignItems="center" justifyContent = "center" direction="column">

            </Flex>
            <Text
            fontSize="4xl"
            fontWeight="bold"
            bgGradient="linear(to-r, cyan.400, blue.500, purple.600)"
            bgClip="text"
          >
            Sanchay Sethi
          </Text>
        </Stack>
    )
}

export default Footer
