                <img src = "https://in.top10place.com/img_files/254088304797637" style = {{width:"60px", height:"60px"}}/>
                <h4>1</h4>
            </div>
            <div class='item'>
                <h4>2</h4>
            </div>

        </OwlCarousel>
        </Stack>

    )
}

export default Clients
