import React, {useState} from 'react'
import { FaSun, FaMoon, FaInstagram, FaGithub, FaLinkedin ,FaBars, FaDownload, FaYoutube} from 'react-icons/fa'

import { useMediaQuery } from '@chakra-ui/media-query';
import { Box, Flex, Heading, Text, Circle, Stack } from '@chakra-ui/layout';
import Icon from '@chakra-ui/icon';
import { DiCodeigniter, DiAndroid, DiWebplatform,DiReact} from 'react-icons/di'
import { ImEarth } from "react-icons/im";
import { useColorMode } from '@chakra-ui/color-mode'
import Terminal, { ColorMode, LineType } from 'react-terminal-ui';


const About = () => {

    const [terminalLineData, setTerminalLineData] = useState([
        {type: LineType.Output, value: 'Welcome to the React Terminal UI Demo!'},
        {type: LineType.Input, value: 'Some previous input received'},
      ]);

    const { colorMode } = useColorMode();
    const isDark = colorMode === "dark";
    const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");

    return (
        <Stack id="aboutsection" style={{overflow: 'hidden'}} justifyContent="center" alignItems="center"  direction={isNotSmallerScreen ? "row" : "column"} w="100%">

                <Terminal style={{width}} name='React Terminal Usage Example' colorMode={ ColorMode.Light }  lineData={ terminalLineData } onInput={ terminalInput => console.log(`New terminal input received: '${ terminalInput }'`) }/>
                <Circle  position="relative" bg="blue.100" opacity={isDark?"0.1":"0.4"} top="20" left="-500"
                w={isNotSmallerScreen?"200px":"450px"} h={isNotSmallerScreen?"200px":"450px"} alignSelf={isNotSmallerScreen ? "flex-start" : "flex-end"} />
                <Circle  position="relative" bg="blue.100" opacity={isDark?"0.1":"0.4"} top="20" left="-500"
                w={isNotSmallerScreen?"100px":"350px"} h={isNotSmallerScreen?"100px":"350px"} alignSelf={isNotSmallerScreen ? "flex-start" : "flex-end"} />
        </Stack>
    )
}

export default About
