import React from "react";
import { Image } from "@chakra-ui/image";
import { useColorMode } from "@chakra-ui/color-mode";
import { useMediaQuery } from "@chakra-ui/media-query";
import { Stack, Circle, Flex, Box, Text, Heading } from "@chakra-ui/layout";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const Skill = () => {
    const { colorMode } = useColorMode();
    const isDark = colorMode === "dark";
    const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
    return (
        <Flex>
            <div className="flexRow align-center mtb-10">
                <div className="skillContainer">
                    <CircularProgressbar
                    value={80}
                    text={`${props.value}%`}
                    styles={buildStyles({
                        textColor: props.textColor,
                        pathColor: props.pathColor,
                    })}
                    />
                </div>

                <div className="mlr-10">
                    <p className="font-16 bold-500 textColor mtb-10">{props.skillName}</p>
                    <p className="font-14 bold-500 grey mtb-10">{props.desc}</p>
                </div>
                </div>

        </Flex>
    )
}

export default Skill
