import React from 'react'
import { Image } from "@chakra-ui/image";
import { Stack, Circle, Flex, Box, Text, Heading} from "@chakra-ui/layout";
import { useMediaQuery } from "@chakra-ui/media-query";
import { useColorMode, useColorModeValue} from "@chakra-ui/color-mode";
import Lottie from "react-lottie";

import logo from "../images/logo2.jpg"

const Footer2 = () => {
    // const defaultOptions = {
    //     loop: true,
    //     autoplay: true,
    //     animationData: plane,
    //     rendererSettings: {
    //       preserveAspectRatio: "xMidYMid slice",
    //     },
    //   };
        const { colorMode } = useColorMode();
        const isDark = colorMode === "dark";
        const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
    return (
        <Stack p = {10} w = "100vw"  justifyContent = "space-evenly" alignItems="center" direction = {isNotSmallerScreen ? "row" : "column"} bg = {isDark? "blue.800" : "blue.100"}>
            <Flex direction = "column" w = "40%">
                <Image src ={logo} w = {40} h = {40} borderRadius = "50%" />
                <Flex w = "100%">
                    <Text fontSize={13}>
                        SACM has a diverse portfolio spread across various industries like IT Campuses, Commercial Spaces, Hotels, Residential Complexes, Factories and Manufacturing Facilities. SACM has created landmark edifices across the nation.
                    </Text>
                </Flex>
            </Flex>
            <Flex direction = "column" w = "40%">
            <Text
                fontSize="5xl"
                fontWeight="bold"
                bgGradient={isDark ?  "linear(to-r, cyan.600, blue.400, purple.300)" :  "linear(to-r, cyan.800, blue.500, purple.400)"} 
                bgClip="text"
                >
                SevenAce Group
            </Text>
                <Flex w = "100%" direction="column">
                    <Text fontSize={20} fontWeight="bold">
                        Contact Us
                    </Text>
                    <Text fontSize={15} fontWeight="bold">
                        <a href="tel:+91-7011228278">✆ +91 7011228278</a><br/>
                        <a href="tel:+91-8800159998">✆ +91 8800159998</a>
                    </Text>
                    <Text mt = {5} fontSize={14} fontWeight="bold">
                        Email
                    </Text>
                    <Text fontSize={15} fontWeight="bold">
                        <a href="mailto:sevenace.group@gmail.com">📨 sevenace.group@gmail.com</a><br/>
                    </Text>
                    <Text mt = {5} fontSize={14} fontWeight="bold">
                        Locate us
                    </Text>
                    <Text fontSize={15} fontWeight="bold">
                        <a href="https://goo.gl/maps/9G3Xi6dvJU8f36QJ8">Plot no. 166, </a><br/>
                    </Text>
                </Flex>
            </Flex>
        </Stack>
    )
}

export default Footer2
