import React from 'react'
import {useState, useEffect } from 'react';
import { IconButton } from "@chakra-ui/button";
import Aos from 'aos'
import 'aos/dist/aos.css'
import { css } from "@emotion/react";
import PacmanLoader from "react-spinners/PacmanLoader";


import { Link, animateScroll as scroll } from "react-scroll";import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,

} from "@chakra-ui/react"
import { Button, ButtonGroup } from "@chakra-ui/react"
import { useColorMode } from "@chakra-ui/color-mode";
import { Flex, VStack, Heading, Spacer, Circle } from "@chakra-ui/layout";
import { FaSun, FaMoon, FaInstagram, FaGithub, FaLinkedin ,FaBars, FaDownload} from 'react-icons/fa'
import ScrollToTop from "react-scroll-to-top";
import Header from "./components/Header";
import Profile from "./components/Profile";
import Social from "./components/Social";
import BottomNav from "./components/BottomNav"
import SideNav from "./components/SideNav"
import About from "./components/About"
import Projects from './components/projects/Projects';
import Skills from './components/skills/Skills';
import Contact from './components/Contact';
import Footer from './components/Footer';
import { useMediaQuery } from '@chakra-ui/media-query';
import "./App.css"

function App() {
  const [loadingAnim, setLoadingAnim] = useState(false)

  const { colorMode, toggleColorMode } = useColorMode();
  const isDark = colorMode === "dark";
  const [isNotSmallerScreen] = useMediaQuery("(min-width:600px)");
  const { isOpen, onOpen, onClose } = useDisclosure()
  const btnRef = React.useRef();

  useEffect(()=>{
    setLoadingAnim(true);
    setTimeout(()=>{
      setLoadingAnim(false)
    },4000)

    Aos.init({duration:2000});
    let progress = document.getElementById('progressbar');
    let totalHeight =document.body.scrollHeight - window.innerHeight;

      window.onscroll = function () {
        let progressHeight = (window.pageYOffset / totalHeight) *100;
        progress.style.height = progressHeight + "%";
      }
  },[])



  return (
    <>
    {
      loadingAnim ? 
      <Flex w= "100%" h = "100vh" justifyContent = "center" alignItems = "center">
        <Circle
        className="animateme1"
        zIndex="-1"
        position="absolute"
        bg="blue.100"
        opacity={isDark ? "0.1" : "0.4"}
        w="300px"
        h="300px"
        top = "0"
        left = "0"
        alignSelf="flex-end"
      />
      <Circle
        className="animateme2"
        zIndex="-1"
        position="absolute"
        bg="blue.100"
        opacity={isDark ? "0.1" : "0.4"}
        w="100px"
        h="100px"
        alignSelf="flex-end"
      />
            <PacmanLoader
                    size = {isNotSmallerScreen ? "40" : "30"}
